mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rn, b.isbn, b.book_title, (SELECT LISTAGG(ba.author, ', ') WITHIN GROUP (ORDER BY ba.author) FROM book_author ba WHERE ba.isbn = b.isbn) AS authors, b.price, b.publisher, b.pub_date, b.image_name, NVL(s.sales_count, 0) AS sales_count, NVL(r.review_count, 0) AS review_count, NVL(b.total_rating, 0) AS total_rating, bc.cat_id -- book_categories FROM book b LEFT JOIN (SELECT od.isbn, SUM(od.quantity) AS sales_count FROM orders_detail od GROUP BY od.isbn) s ON b.isbn = s.isbn LEFT JOIN (SELECT r.isbn, COUNT(r.review_id) AS review_count FROM review r GROUP BY r.isbn) r ON b.isbn = r.isbn LEFT JOIN book_categories bc ON b.isbn = bc.isbn WHERE bc.cat_id =#{cat_id, jdbcType=VARCHAR}
ORDER BY sales_count DESC
ORDER BY review_count DESC
ORDER BY total_rating DESC
ORDER BY b.pub_date DESC
) a WHERE a.rn > #{start} AND a.rn <= #{end}

WITH Category_Hierarchy (cat_id) AS ( SELECT cat_id FROM categories WHERE parent_id = #{parent_id} UNION ALL SELECT c.cat_id FROM categories c JOIN Category_Hierarchy ch ON c.parent_id = ch.cat_id ), Author_List AS ( SELECT ba.isbn, LISTAGG(ba.author, ', ') WITHIN GROUP (ORDER BY ba.author) AS authors FROM book_author ba GROUP BY ba.isbn ) SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY b.pub_date DESC) AS row_num, -- PARTITION BY b.isbn 제거 b.isbn, b.book_title, al.authors, b.price, b.publisher, b.pub_date, b.image_name, NVL(s.sales_count, 0) AS sales_count, NVL(r.review_count, 0) AS review_count, NVL(b.total_rating, 0) AS total_rating, bc.cat_id FROM book b LEFT JOIN book_categories bc ON b.isbn = bc.isbn LEFT JOIN Category_Hierarchy ch ON bc.cat_id = ch.cat_id LEFT JOIN Author_List al ON b.isbn = al.isbn LEFT JOIN (SELECT od.isbn, SUM(od.quantity) AS sales_count FROM orders_detail od GROUP BY od.isbn) s ON b.isbn = s.isbn LEFT JOIN (SELECT r.isbn, COUNT(r.review_id) AS review_count FROM review r GROUP BY r.isbn) r ON b.isbn = r.isbn WHERE (bc.cat_id IN (SELECT cat_id FROM Category_Hierarchy) OR bc.cat_id = #{parent_id})
ORDER BY sales_count DESC
ORDER BY review_count DESC
ORDER BY total_rating DESC
ORDER BY b.pub_date DESC
) a WHERE a.row_num > #{start} AND a.row_num <= #{end}
