mapper.select:
SELECT * FROM ( SELECT b.*, rownum rn FROM ( SELECT b.isbn, b.price, b.publisher, b.book_title, b.image_name, b.authors FROM ( SELECT b.isbn, b.price, b.publisher, b.book_title, b.image_name, LISTAGG(ba.author, ', ') WITHIN GROUP (ORDER BY ba.author) AS authors FROM book b JOIN book_author ba ON b.isbn = ba.isbn WHERE b.isbn IN ( SELECT DISTINCT bc.isbn FROM book_categories bc JOIN categories c ON bc.cat_id = c.cat_id
where c.cat_id in (select cat_id from categories where parent_id in (select cat_id from categories where parent_id = #{cat_id} or cat_id = #{cat_id}))
where c.parent_id = #{cat_id} or c.cat_id = #{cat_id}
where bc.cat_id = #{cat_id}
)
AND b.book_title LIKE '%' || #{book_title} || '%'
GROUP BY b.isbn, b.price, b.publisher, b.book_title, b.image_name ) b ORDER BY b.book_title ) b ) WHERE rn > #{start} AND rn < #{end}

SELECT COUNT(DISTINCT b.isbn) FROM book b JOIN book_categories bc ON b.isbn = bc.isbn JOIN categories c ON bc.cat_id = c.cat_id WHERE 1=1
AND c.cat_id IN (SELECT cat_id FROM categories WHERE parent_id IN (SELECT cat_id FROM categories WHERE parent_id = #{cat_id} OR cat_id = #{cat_id}) )
AND c.parent_id = #{cat_id} or c.cat_id = #{cat_id}
AND bc.cat_id = #{cat_id}
AND b.book_title LIKE '%' || #{book_title} || '%'
